<?php

/**
 * Plugin admin menu
 *
 * @return void
 */
function AV_CS_admin_menus() {

	$av_plugins 		= avthemes_get_plugins();
	$parent_menu_slug	= 'options-general.php';

	if ( count($av_plugins) > 1 ) {
		
		if( empty ( $GLOBALS['admin_page_hooks']['AV_themes_plugin_options'] ) ) {

			add_menu_page( 
				__( 'AV Themes & Plugins Options', 'av-coming-soon' ), // page title
				__( 'AV Settings', 'av-coming-soon' ), // menu title
				'edit_theme_options', // user capability
				'AV_themes_plugin_options',	// menu slug
				'AV_Plugins_plugin_options_page', // menu callback function
				AV_Options_Menu_Icon() // parent menu icon
			);
		}

		$parent_menu_slug	= 'AV_themes_plugin_options';
	}

	add_submenu_page(
		$parent_menu_slug, // parent slug
		__( 'AV Coming Soon Options', 'av-coming-soon' ), // page title
		__( 'Coming Soon', 'av-coming-soon' ), // menu title
		'edit_theme_options', // user capability
		AV_CS_PLUGIN_SETTING_PAGE,	// menu slug
		'AV_CS_plugin_options_page', // menu callback function
		100 // position
	);
}

/**
 * Admin menu icon
 *
 * @return void
 */

 if( ! function_exists( 'AV_Options_Menu_Icon' ) ) {

	function AV_Options_Menu_Icon() {

		return 'data:image/svg+xml;base64,';
	}
}